import React from 'react';
import { useEffect, useRef } from 'react';


const centerX = 150;
const centerY = 150;
const body_margin = 0;
const distanceFromCenter = -10;
let x = centerX + distanceFromCenter*Math.cos(0);
let y = centerY + distanceFromCenter*Math.sin(0);
let angleRad = 0;
const ballRadius = 20;

export function QFormat(){
    const canvasRef = useRef(null);
    useEffect(() =>{
        const canvas = canvasRef.current;
        let rect = e.target.getBoundingClientRect() ;
        let X = e.clientX - rect.left;
        let Y = e.clientY - rect.top;
        radius = Math.atan2(centerX - X,centerY - Y);
        x = - distanceFromCenter* Math.cos(radius) + centerX + body_margin;
        y = - distanceFromCenter* Math.sin(radius) + centerY + body_margin;
        let ctx = canvas.getContext('2d');
        ctx.clearRect(0, 0, canvas.width, canvas.height);
        ctx.fillStyle = "#0095DD";
        ctx.beginPath();
        ctx.arc(x, y, ballRadius, 0, Math.PI*2);        
        ctx.fill();
        ctx.closePath();
        console.log(Math.PI);
        },[]
    );

    return(
        <div>
            <canvas className="canvas" id="canvas" ref={canvasRef}></canvas>
        </div>
    );
}

export function MoveHandle(){
    useEffect(() =>{
        window.addEventListener("click",(e) => {
            if(e.target.id=="canvas"){
            const canvasRef = useRef(e.target);
            const canvas = canvasRef.current;

            let ctx = canvas.getContext('2d');
            ctx.clearRect(0, 0, canvas.width, canvas.height);
            ctx.fillStyle = "#0095DD";
            ctx.beginPath();
            ctx.arc(x, y, ballRadius, 0, Math.PI*2);        
            ctx.fill();
            ctx.closePath();
            console.log(Math.PI);
            }
        }

        );

    },[]
    );
}

export default QFormat;
